"use client";
import React, { useEffect, useMemo, useState } from "react";
import { animate, motion } from "framer-motion";
import { cn } from "@workspace/ui/lib/utils";

export function TechCardBackend() {
  return (
    <Card className="mb-6">
      <CardSkeletonContainer>
        <Skeleton />
      </CardSkeletonContainer>
      <CardTitle>Back End</CardTitle>
      <CardDescription>Technologies Used in The Bakcend</CardDescription>
    </Card>
  );
}

const Skeleton = () => {
  const [isClient, setIsClient] = useState(false);

  const scale = useMemo(() => [1, 1.1, 1], []);
  const transform = useMemo(
    () => ["translateY(0px)", "translateY(-4px)", "translateY(0px)"],
    []
  );

  const sequence = useMemo(() => {
    return [
      [".circle-1", { scale, transform }, { duration: 0.8 }],
      [".circle-2", { scale, transform }, { duration: 0.8 }],
      [".circle-3", { scale, transform }, { duration: 0.8 }],
      [".circle-4", { scale, transform }, { duration: 0.8 }],
      [".circle-5", { scale, transform }, { duration: 0.8 }],
    ];
  }, [scale, transform]);

  useEffect(() => {
    setIsClient(true);

    if (typeof window !== "undefined") {
      const animationController = animate(sequence, {
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-expect-error
        repeat: Infinity,
        repeatDelay: 1,
      });

      return () => {
        animationController.stop();
      };
    }
  }, [sequence]);

  return (
    <div className="p-8 overflow-hidden h-full mb-4 relative flex items-center justify-center">
      <div className="flex flex-row flex-shrink-0 justify-center items-center gap-2">
        <Container className="h-8 w-8 circle-1">
          <PrismaLogo className="h-12 w-12 circle-2" />
        </Container>
        <Container className="h-12 w-12 circle-2">
          <CopilotLogo className="h-6 w-6 text-black fill-black" />
        </Container>
        <Container className="circle-3">
          <OpenAILogo className="h-8 w-8 dark:text-white" />
        </Container>
        <Container className="h-12 w-12 circle-4">
          <MetaIconOutline className="h-6 w-6" />
        </Container>
        <Container className="h-8 w-8 circle-5">
          <GeminiLogo className="h-4 w-4" />
        </Container>
      </div>

      {isClient && (
        <div className="h-40 w-px absolute top-20 m-auto z-40 bg-gradient-to-b from-transparent via-cyan-500 to-transparent animate-move">
          <div className="w-10 h-32 top-1/2 -translate-y-1/2 absolute -left-10">
            <Sparkles />
          </div>
        </div>
      )}
    </div>
  );
};

const Sparkles = () => {
  const [sparkles, setSparkles] = useState<
    {
      id: number;
      top: number;
      left: number;
      opacity: number;
    }[]
  >([]);

  useEffect(() => {
    const newSparkles = [...Array(12)].map((_, i) => ({
      id: i,
      top: Math.random() * 100,
      left: Math.random() * 100,
      opacity: Math.random(),
    }));
    setSparkles(newSparkles);
  }, []);

  return (
    <div className="absolute inset-0">
      {sparkles.map((sparkle) => (
        <motion.span
          key={`star-${sparkle.id}`}
          animate={{
            top: `calc(${sparkle.top}% + ${Math.random() * 2 - 1}px)`,
            left: `calc(${sparkle.left}% + ${Math.random() * 2 - 1}px)`,
            opacity: [sparkle.opacity, 0.5, 0],
            scale: [1, 1.2, 0],
          }}
          transition={{
            duration: Math.random() * 2 + 4,
            repeat: Infinity,
            ease: "linear",
          }}
          style={{
            position: "absolute",
            top: `${sparkle.top}%`,
            left: `${sparkle.left}%`,
            width: `2px`,
            height: `2px`,
            borderRadius: "50%",
            zIndex: 1,
          }}
          className="inline-block bg-black dark:bg-white"
        ></motion.span>
      ))}
    </div>
  );
};

